[class*="jumpToPresentBar"] {
  display: contents;
}
[class*="jumpToPresentBar"] > button:first-child {
  display: none;
}
[class*="jumpToPresentBar"] > button:last-child {
  position: absolute;
  bottom: 24px; /* fixed the position relative to right part */
  right: 22px; /* so it doesn't look so close/odd */
  padding: 8px !important;
  height: auto;
  background-color: var(
    --background-secondary
  ); /* changed color to darker gray */
  /*box-shadow: var(--elevation-high); I don't like the shadow */
  border-radius: 100px;
  display: flex;
  font-size: 0;
  color: transparent;
  transition: font-size 0.2s 0.1s, color 0.2s, padding 0.4s 0.1s;
}
[class*="jumpToPresentBar"] > button svg {
  order: -1;
  margin: 0;
  height: 24px;
  width: 24px;
  top: unset;
  margin-right: 4px;
  margin-left: 0;
  margin-right: 0;
  color: var(--text-normal);
  transition: margin 0.6s;
}

[class*="jumpToPresentBar"]:hover > button {
  padding: 8px 18px !important;
  font-size: 14px;
  color: var(--text-normal);
  transition: font-size 0.2s, color 0.2s 0.1s, padding 0.4s;
}
[class*="jumpToPresentBar"]:hover > button svg {
  margin-right: 2px;
  margin-left: -6px;
}
